package com.example;import java.util.Scanner;/** * 作用 : 简单的+-* /的运算实现 * 作者 ：张桐源 * 日期： 2016/11/28 */public class MyClass {    public static double GetResult(double numA, double numB, String opr) {        double result = 0d;        switch (opr) {            case "+":                result = numA + numB;                break;            case "-":                result = numA - numB;                break;            case "*":                result = numA * numB;                break;            case "/":                result = numA / numB;                break;        }        return result;    }    public static void main(String[] args) {        System.out.println("请输入数字A:");        Scanner scan = new Scanner(System.in);        String strNumA = scan.nextLine();        System.out.println("请输入运算符号(+、-、*、/):");        String strOpr = scan.nextLine();        System.out.println("请输入数字B");        String strNumB = scan.nextLine();        String strResult = "";        double Result = GetResult(Double.parseDouble(strNumA), Double.parseDouble(strNumB), strOpr);        strResult = String.valueOf(Result);        System.out.println(strResult);    }}